'use strict';

module.exports = function expected(val) {
  var fns = {to: {}};
  fns.to.be = function (msg) {
    throw new TypeError('expected "' + val + '" to be ' + msg);
  };

  fns.to.be.a = function (msg) {
    throw new TypeError('expected "' + val + '" to be a ' + msg);
  };

  fns.to.be.an = function (msg) {
    throw new TypeError('expected "' + val + '" to be an ' + msg);
  };

  fns.to.have = function (key) {
    if (!val.hasOwnProperty(key)) {
      throw new TypeError('expected "' + val + '" to have "' + key + '"');
    }
  };
  return fns;
};
